import React, { useState, useEffect } from 'react';
import './CourseRegistration.css';
import DepartmentInfo from './DepartmentInfo';
import FacultyInfo from './FacultyInfo';
import ProgramOfferings from './ProgramOfferings';
import CourseModules from './CourseModules';
import RegistrationForm from './RegistrationForm';

const CourseRegistration: React.FC = () => {
    const [activeTab, setActiveTab] = useState<string>('department');
    const [isPageLoaded, setIsPageLoaded] = useState<boolean>(false);
    const [registeredCourses, setRegisteredCourses] = useState<string[]>([]);
    const [message, setMessage] = useState<string>('');

    useEffect(() => {
        setIsPageLoaded(true);
    }, []);

    const handleRegisterCourse = (course: string) => {
        if (course && !registeredCourses.includes(course)) {
            setRegisteredCourses([...registeredCourses, course]);
            setMessage(`Successfully registered for ${course}`);
        } else if (registeredCourses.includes(course)) {
            setMessage('You are already registered for this course.');
        } else {
            setMessage('Please select a course to register.');
        }
    };

    const handleRemoveCourse = (course: string) => {
        setRegisteredCourses(registeredCourses.filter(c => c !== course));
        setMessage(`Successfully removed ${course}`);
    };

    return (
        <div className={`course-registration ${isPageLoaded ? 'loaded' : ''}`}>
            <div className="registration-header">
                <h1>Mathematics and Computing Department</h1>
                <p>Walter Sisulu University</p>
            </div>

            <div className="registration-navigation">
                <button 
                    className={activeTab === 'department' ? 'active' : ''} 
                    onClick={() => setActiveTab('department')}>
                    Department Overview
                </button>
                <button 
                    className={activeTab === 'faculty' ? 'active' : ''} 
                    onClick={() => setActiveTab('faculty')}>
                    Faculty & Staff
                </button>
                <button 
                    className={activeTab === 'programs' ? 'active' : ''} 
                    onClick={() => setActiveTab('programs')}>
                    Programs
                </button>
                <button 
                    className={activeTab === 'courses' ? 'active' : ''} 
                    onClick={() => setActiveTab('courses')}>
                    Course Modules
                </button>
                <button 
                    className={activeTab === 'register' ? 'active' : ''} 
                    onClick={() => setActiveTab('register')}>
                    Register for Courses
                </button>
            </div>

            <div className="registration-content">
                {activeTab === 'department' && <DepartmentInfo />}
                {activeTab === 'faculty' && <FacultyInfo />}
                {activeTab === 'programs' && <ProgramOfferings />}
                {activeTab === 'courses' && <CourseModules />}
                {activeTab === 'register' && (
                    <RegistrationForm 
                        registeredCourses={registeredCourses}
                        onRegister={handleRegisterCourse}
                        onRemove={handleRemoveCourse}
                        message={message}
                    />
                )}
            </div>
        </div>
    );
};

export default CourseRegistration;
