import React, { useState, useEffect } from 'react';
import './FoodOrdering.css';

interface MenuItem {
  id: string;
  name: string;
  price: number;
  category: string;
  description?: string;
  image?: string;
}

interface CartItem extends MenuItem {
  quantity: number;
}

interface OrderModalProps {
  cart: CartItem[];
  total: number;
  pickupTime: string;
  onClose: () => void;
  onConfirm: () => void;
}

interface PaymentModalProps {
  total: number;
  onClose: () => void;
  onPaymentComplete: (paymentMethod: string) => void;
}

// Order Modal Component
const OrderModal: React.FC<OrderModalProps> = ({ cart, total, pickupTime, onClose, onConfirm }) => {
  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>Order Confirmation</h2>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>
        <div className="modal-content">
          <h3>Order Summary</h3>
          {cart.map((item) => (
            <div className="order-item" key={item.id}>
              <span>{item.name} × {item.quantity}</span>
              <span>R{item.price * item.quantity}</span>
            </div>
          ))}
          <div className="order-total">
            <span>Total:</span>
            <span>R{total}</span>
          </div>
          <div className="pickup-info">
            <p><strong>Pickup Time:</strong> {pickupTime === 'asap' ? 'As soon as possible' : pickupTime}</p>
          </div>
          <button className="confirm-btn" onClick={onConfirm}>
            Confirm Order
          </button>
        </div>
      </div>
    </div>
  );
};

// Payment Modal Component
const PaymentModal: React.FC<PaymentModalProps> = ({ total, onClose, onPaymentComplete }) => {
  const [paymentMethod, setPaymentMethod] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  const handlePayNow = () => {
    if (!paymentMethod) return;

    setIsProcessing(true);
    // Simulate payment processing
    setTimeout(() => {
      setIsProcessing(false);
      onPaymentComplete(paymentMethod);
    }, 1500);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>Payment</h2>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>
        <div className="modal-content">
          <h3>Select Payment Method</h3>
          <div className="payment-options">
            <label>
              <input
                type="radio"
                name="payment"
                value="cash"
                checked={paymentMethod === 'cash'}
                onChange={() => setPaymentMethod('cash')}
              />
              <span>Pay with Cash on Pickup</span>
            </label>
            <label>
              <input
                type="radio"
                name="payment"
                value="card"
                checked={paymentMethod === 'card'}
                onChange={() => setPaymentMethod('card')}
              />
              <span>Pay with Card Online</span>
            </label>
          </div>

          {paymentMethod === 'card' && (
            <div className="card-details">
              <div className="form-group">
                <label>Card Number</label>
                <input type="text" placeholder="1234 5678 9012 3456" />
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>Expiry Date</label>
                  <input type="text" placeholder="MM/YY" />
                </div>
                <div className="form-group">
                  <label>CVV</label>
                  <input type="text" placeholder="123" />
                </div>
              </div>
            </div>
          )}

          <div className="payment-total">
            <span>Total to Pay:</span>
            <span>R{total}</span>
          </div>

          <button 
            className={`pay-btn ${isProcessing ? 'processing' : ''}`}
            onClick={handlePayNow}
            disabled={!paymentMethod || isProcessing}
          >
            {isProcessing ? 'Processing...' : paymentMethod === 'cash' ? 'Confirm Cash Payment' : 'Pay Now'}
          </button>
        </div>
      </div>
    </div>
  );
};

const FoodOrdering: React.FC = () => {
  const [menuItems, setMenuItems] = useState<MenuItem[]>([
    // Main Menu
    { id: 'cm1', name: 'Chicken Meal', price: 50, category: 'Main Menu', description: 'Delicious chicken meal with sides' },
    { id: 'bm1', name: 'Beef Meal', price: 60, category: 'Main Menu', description: 'Hearty beef meal with vegetables' },
    { id: 'cl1', name: 'Chicken Livers', price: 25, category: 'Main Menu' },
    { id: 'sg1', name: 'Samp & Gravy', price: 20, category: 'Main Menu' },
    { id: 'bs1', name: 'Beef Stew', price: 40, category: 'Main Menu' },
    { id: 'cs1', name: 'Chicken Stew', price: 35, category: 'Main Menu' },
    { id: 'ch1', name: 'Chicken Hearts', price: 20, category: 'Main Menu' },
    { id: 's1', name: 'Sausage', price: 30, category: 'Main Menu' },

    // Extras
    { id: 'fs1', name: 'Fruit Salad (with yogurt)', price: 35, category: 'Extras' },
    { id: 'p1', name: 'Pasta', price: 30, category: 'Extras' },
    { id: 'u1', name: 'Ulusu', price: 30, category: 'Extras' },
    { id: 'po1', name: 'Pork', price: 35, category: 'Extras' },
    { id: 'bst1', name: 'Beef Steak', price: 45, category: 'Extras' },
    { id: 'wo1', name: 'Wings Only', price: 35, category: 'Extras' },
    { id: 'ql1', name: 'Q-Leg', price: 33, category: 'Extras' },
    { id: 'la1', name: 'Lasagne', price: 35, category: 'Extras' },
    { id: 'cw1', name: 'Chicken Wrap', price: 35, category: 'Extras' },

    // Bar Items
    { id: 'pi1', name: 'Pies', price: 23, category: 'Bar Items' },
    { id: 'sb1', name: 'Steam Bread', price: 8, category: 'Bar Items' },
    { id: 'sw1', name: 'Sandwiches', price: 17, category: 'Bar Items' },
    { id: 'gi1', name: 'Gizzards', price: 25, category: 'Bar Items' },
    { id: 'ol1', name: 'Occ. Liver', price: 30, category: 'Bar Items' },
    { id: 'bu1', name: 'Burgers', price: 40, category: 'Bar Items' },
    { id: 'wc1', name: 'Wings & Chips', price: 45, category: 'Bar Items' },
    { id: 'qc1', name: 'Quarter Leg & Chips', price: 40, category: 'Bar Items' },
  ]);

  const [cart, setCart] = useState<CartItem[]>([]);
  const [activeCategory, setActiveCategory] = useState('Main Menu');
  const [search, setSearch] = useState('');
  const [isOrderModalOpen, setIsOrderModalOpen] = useState(false);
  const [isPaymentModalOpen, setIsPaymentModalOpen] = useState(false);
  const [orderCompleted, setOrderCompleted] = useState(false);
  const [pickupTime, setPickupTime] = useState('');

  const addToCart = (item: MenuItem) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);
    if (existingItem) {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        )
      );
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }

    // Animation trigger for the item
    const element = document.getElementById(item.id);
    if (element) {
      element.classList.add('added-to-cart');
      setTimeout(() => {
        element?.classList.remove('added-to-cart');
      }, 500);
    }
  };

  const removeFromCart = (itemId: string) => {
    const existingItem = cart.find((item) => item.id === itemId);
    if (existingItem && existingItem.quantity > 1) {
      setCart(
        cart.map((item) =>
          item.id === itemId ? { ...item, quantity: item.quantity - 1 } : item
        )
      );
    } else {
      setCart(cart.filter((item) => item.id !== itemId));
    }
  };

  const clearCart = () => {
    setCart([]);
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handlePickupTimeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setPickupTime(e.target.value);
  };

  const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearch(e.target.value);
  };

  const handleCheckout = () => {
    setIsOrderModalOpen(true);
  };

  const handleOrderConfirm = () => {
    setIsOrderModalOpen(false);
    setIsPaymentModalOpen(true);
  };

  const handlePaymentComplete = (paymentMethod: string) => {
    setIsPaymentModalOpen(false);
    setOrderCompleted(true);
    // Reset after 5 seconds
    setTimeout(() => {
      setOrderCompleted(false);
      clearCart();
    }, 5000);
  };

  const filteredItems = menuItems.filter(item => 
    (activeCategory === 'All' || item.category === activeCategory) &&
    (search === '' || item.name.toLowerCase().includes(search.toLowerCase()))
  );

  // Create categories array - alternative to using Set to avoid TS errors
  const uniqueCategories = ['All'];
  menuItems.forEach(item => {
    if (!uniqueCategories.includes(item.category)) {
      uniqueCategories.push(item.category);
    }
  });

  return (
    <div className="food-ordering-container">
      <div className="food-header">
        <h1>University Restaurant</h1>
        <p>Skip the line. Order ahead!</p>

        <div className="search-container">
          <input 
            type="text" 
            placeholder="Search menu..." 
            value={search}
            onChange={handleSearch}
            className="search-input"
          />
          <i className="fas fa-search search-icon"></i>
        </div>
      </div>

      <div className="categories-container">
        {uniqueCategories.map(category => (
          <button
            key={category}
            className={`category-btn ${activeCategory === category ? 'active' : ''}`}
            onClick={() => setActiveCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="menu-container">
        <div className="menu-items">
          {filteredItems.map((item) => (
            <div className="menu-item" key={item.id} id={item.id}>
              <div className="item-info">
                <h3>{item.name}</h3>
                <p className="price">R{item.price}</p>
                {item.description && <p className="description">{item.description}</p>}
              </div>
              <button 
                className="add-btn"
                onClick={() => addToCart(item)}
              >
                <i className="fas fa-plus"></i>
              </button>
            </div>
          ))}
        </div>

        <div className="cart-container">
          <div className="cart-header">
            <h2>Your Order</h2>
            {cart.length > 0 && (
              <button className="clear-btn" onClick={clearCart}>
                Clear
              </button>
            )}
          </div>

          {cart.length === 0 ? (
            <div className="empty-cart">
              <i className="fas fa-shopping-basket"></i>
              <p>Your cart is empty</p>
              <p className="empty-cart-subtext">Add items from the menu to get started</p>
            </div>
          ) : (
            <>
              <div className="cart-items">
                {cart.map((item) => (
                  <div className="cart-item" key={item.id}>
                    <div className="cart-item-info">
                      <span className="item-name">{item.name}</span>
                      <span className="item-price">R{item.price}</span>
                    </div>
                    <div className="quantity-control">
                      <button onClick={() => removeFromCart(item.id)}>-</button>
                      <span>{item.quantity}</span>
                      <button onClick={() => addToCart(item)}>+</button>
                    </div>
                  </div>
                ))}
              </div>

              <div className="pickup-time">
                <label htmlFor="pickup-time">Pickup Time:</label>
                <select 
                  id="pickup-time" 
                  value={pickupTime} 
                  onChange={handlePickupTimeChange}
                  required
                >
                  <option value="">Select time</option>
                  <option value="asap">As soon as possible</option>
                  <option value="30min">30 minutes</option>
                  <option value="1hour">1 hour</option>
                  <option value="2hours">2 hours</option>
                </select>
              </div>

              <div className="cart-total">
                <span>Total:</span>
                <span>R{getTotalPrice()}</span>
              </div>

              <button 
                className="checkout-btn"
                onClick={handleCheckout}
                disabled={cart.length === 0 || pickupTime === ''}
              >
                Checkout
              </button>
            </>
          )}
        </div>
      </div>

      {/* Order Confirmation Modal */}
      {isOrderModalOpen && (
        <OrderModal 
          cart={cart} 
          total={getTotalPrice()} 
          pickupTime={pickupTime}
          onClose={() => setIsOrderModalOpen(false)}
          onConfirm={handleOrderConfirm}
        />
      )}

      {/* Payment Modal */}
      {isPaymentModalOpen && (
        <PaymentModal 
          total={getTotalPrice()}
          onClose={() => setIsPaymentModalOpen(false)}
          onPaymentComplete={handlePaymentComplete}
        />
      )}

      {/* Order Success Message */}
      {orderCompleted && (
        <div className="order-success">
          <div className="success-content">
            <i className="fas fa-check-circle"></i>
            <h2>Order Placed Successfully!</h2>
            <p>Your order has been received.</p>
            <p>Check your email for confirmation.</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default FoodOrdering;
