import React, { useState } from 'react';
import './CourseModules.css';

interface Module {
    id: number;
    code: string;
    name: string;
    level: number;
    credits: number;
    program: string[];
    semester: number;
    description: string;
    prerequisites: string[];
    outcomes: string[];
    assessment: string;
    courseConvener?: string;
    offeringSite?: string;
}

const CourseModules: React.FC = () => {
    const [selectedProgram, setSelectedProgram] = useState<string>('all');
    const [selectedLevel, setSelectedLevel] = useState<number>(0);
    const [selectedSemester, setSelectedSemester] = useState<number>(0);
    const [searchQuery, setSearchQuery] = useState<string>('');
    const [expandedModule, setExpandedModule] = useState<number | null>(null);

    const modules: Module[] = [
        {
            id: 1,
            code: "MAT1541",
            name: "Mathematics I",
            level: 1,
            credits: 16,
            program: ["BSCMATH", "BSCAMAT", "BSCASCS", "BSCCOMP"],
            semester: 1,
            description: "This foundational course introduces students to key mathematical concepts including functions, limits, derivatives, and integrals. It forms the basis for further study in mathematics and related fields.",
            prerequisites: [],
            outcomes: [
                "Apply the concept of limits to analyze functions",
                "Calculate derivatives using various techniques",
                "Apply derivatives to solve optimization problems",
                "Calculate basic integrals and apply them to area problems"
            ],
            assessment: "Assignments (20%), Tests (30%), Examination (50%)"
        },
        {
            id: 2,
            code: "MAT1642",
            name: "Mathematics II",
            level: 1,
            credits: 16,
            program: ["BSCMATH", "BSCAMAT", "BSCASCS"],
            semester: 2,
            description: "Building on Mathematics I, this course covers techniques of integration, sequences and series, and an introduction to multivariable calculus.",
            prerequisites: ["MAT1541"],
            outcomes: [
                "Apply various integration techniques",
                "Analyze convergence of sequences and series",
                "Evaluate improper integrals",
                "Solve basic differential equations",
                "Work with functions of several variables"
            ],
            assessment: "Assignments (20%), Practical Work (10%), Tests (30%), Examination (40%)"
        },
        {
            id: 3,
            code: "STA1541",
            name: "Introduction to Statistics",
            level: 1,
            credits: 16,
            program: ["BSCASCS", "BSCAMAT", "BSCCOMP"],
            semester: 1,
            description: "This course introduces fundamental statistical concepts including descriptive statistics, probability, random variables, and common distributions. Students will learn to collect, organize, analyze, and interpret data.",
            prerequisites: [],
            outcomes: [
                "Calculate and interpret descriptive statistics",
                "Apply principles of probability to solve problems",
                "Identify and apply probability distributions",
                "Perform hypothesis testing for one and two samples",
                "Interpret statistical results and draw conclusions"
            ],
            assessment: "Assignments (15%), Practical Work (15%), Tests (20%), Examination (50%)"
        },
        {
